mapper.update:
UPDATE portfolio_board
SET bod_CNT = bod_CNT+1
WHERE bod_NO=#{bod_NO};

mapper.select:
SELECT bod_NO, bod_TITLE, bod_CONTENT
FROM portfolio_board
WHERE bod_NO=#{bod_NO};

mapper.update:
UPDATE portfolio_board
SET bod_TITLE=#{bod_TITLE}, bod_CONTENT=#{bod_CONTENT}
WHERE bod_NO=#{bod_NO};
